    function copyToClipBoard(jQueryCodeElement) {

        let clipboard = document.getElementById('clipboard');

        clipboard.value = jQueryCodeElement.parent().children('code').text();

        clipboard.select();
        //clipboard.setSelectionRange(0, 99999);
        document.execCommand('copy');
    }

    // Avoid scroll on first click
    document.getElementById('clipboard').select();

    jQuery('p code').each(function() {

        let copyLink = jQuery(' <a class="btn btn-sm btn-link" href="" title="Copy to clipboard"><span class="glyphicon glyphicon-copy"></span></a>');

        jQuery(this).parent().append(copyLink);

        copyLink.click(function(event) {

            event.preventDefault();
            event.stopPropagation();

            copyToClipBoard(jQuery(this));

            return false;
        });

    });


