    function toggleMenu() {
      var $sidebar = $("#sidebar");
      var $overlay = $("#overlay");

      if ($sidebar.hasClass("open")) {
        $sidebar.removeClass("open").css("left", "-255px");
        $overlay.removeClass("show");
      } else {
        $sidebar.addClass("open").css("left", "0");
        $overlay.addClass("show");
      }
    }

    $("#sidebar ul li a").on("click", function(event) {
        // Vérifie si l'élément cliqué a un sous-menu (ul enfant)
        if ($(this).siblings("ul").length > 0) {
            // Empêche la fermeture du sidebar si un sous-menu est présent
            event.preventDefault();
        } else {
            // Ferme le sidebar s'il n'y a pas de sous-menu
            $("#sidebar").removeClass("open").css("left", "-255px");
            $("#overlay").removeClass("show");
        }
    });








